mapper.delete:
delete from subs
where member_no = #{memberNo}
and brand_no = #{brandNo}

mapper.update:
update brand
set subscount = subscount - 1
where no = #{brandNo}

update brand
set subscount = subscount + 1
where no = #{brandNo}

mapper.select:
select count(*)
from zzim
where member_no = #{memberNo}
and product_no = #{productNo}

mapper.update:
UPDATE product
SET zzimcount = zzimcount + 1
WHERE no = #{productNo}
