mapper.update:
		UPDATE RESERVATION
       	   SET STATUS = 1
     	 WHERE NO = #{no}
     	   AND BRAND_NO = #{brandNo}
     	   AND STORE_NO = #{storeNo}

		UPDATE RESERVATION
       	   SET STATUS = 3
     	 WHERE NO = #{no}
     	   AND BRAND_NO = #{brandNo}
     	   AND STORE_NO = #{storeNo}

		UPDATE RESERVATION
       	   SET STATUS = 2
     	 WHERE NO = #{no}
     	   AND BRAND_NO = #{brandNo}
     	   AND STORE_NO = #{storeNo}

mapper.select:
SELECT M.NAME AS TITLE,
       TO_CHAR(R.RESTIME, 'YYYY-MM-DD HH24:MI') AS "START"
  FROM RESERVATION R
  JOIN MEMBER M
    ON R.MEMBER_NO = M.NO
 WHERE R.BRAND_NO = #{brandNo}
   AND R.STORE_NO = #{storeNo}
   AND STATUS = 1
   AND TO_CHAR(R.RESTIME, 'YYYY-MM') = #{date}
